import React, { useState, useEffect } from 'react';
import { MapPin, AlertTriangle, Shield, Construction, Thermometer, Navigation as NavigationIcon, Settings, Bell } from 'lucide-react';
import Health from './Health';
import Navigation from './Navigation';

const TrafficHealthApp = () => {
  const [currentTime, setCurrentTime] = useState(new Date());
  const [heatIndex, setHeatIndex] = useState(89);
  const [temperature, setTemperature] = useState(92);
  const [humidity, setHumidity] = useState(65);
  const [showPopup, setShowPopup] = useState(true);
  const [currentPage, setCurrentPage] = useState('home'); // home, navigate, health

  useEffect(() => {
    const timer = setInterval(() => setCurrentTime(new Date()), 1000);
    return () => clearInterval(timer);
  }, []);

  // Simulate real-time data updates
  useEffect(() => {
    const interval = setInterval(() => {
      setHeatIndex(prev => prev + (Math.random() - 0.5) * 2);
      setTemperature(prev => prev + (Math.random() - 0.5) * 1.5);
      setHumidity(prev => Math.max(30, Math.min(90, prev + (Math.random() - 0.5) * 3)));
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  const getHeatIndexColor = (index) => {
    if (index < 80) return 'text-green-600 bg-green-100';
    if (index < 90) return 'text-yellow-600 bg-yellow-100';
    if (index < 105) return 'text-orange-600 bg-orange-100';
    return 'text-red-600 bg-red-100';
  };

  const getHeatIndexWarning = (index) => {
    if (index < 80) return 'Safe conditions';
    if (index < 90) return 'Caution advised';
    if (index < 105) return 'Take frequent breaks';
    return 'Extreme caution - limit exposure';
  };


  // If health page is selected, show the health component
  if (currentPage === 'health') {
    return <Health onBack={() => setCurrentPage('home')} />;
  }

  // If navigate page is selected, show the navigation component
  if (currentPage === 'navigate') {
    return <Navigation currentTime={currentTime} onBack={() => setCurrentPage('home')} />;
  }

  const trafficAlerts = [
    { id: 1, type: 'accident', location: 'I-95 Northbound Mile 45', distance: '0.8 mi', time: '5 min ago', icon: AlertTriangle, color: 'red' },
    { id: 2, type: 'construction', location: 'Route 1 & Main St', distance: '1.2 mi', time: 'Ongoing', icon: Construction, color: 'orange' },
    { id: 3, type: 'police', location: 'Highway 50 Exit 12', distance: '2.1 mi', time: '15 min ago', icon: Shield, color: 'blue' },
    { id: 4, type: 'traffic', location: 'Downtown Bridge', distance: '0.5 mi', time: 'Live', icon: MapPin, color: 'yellow' }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-100 to-gray-100 p-4 relative overflow-hidden">
      {/* Background decorative elements */}
      <div className="absolute top-10 right-10 w-32 h-32 bg-purple-200 rounded-full opacity-20 blur-xl"></div>
      <div className="absolute bottom-20 left-10 w-24 h-24 bg-indigo-200 rounded-full opacity-15 blur-lg"></div>
      <div className="absolute top-1/2 left-1/4 w-16 h-16 bg-purple-300 rounded-full opacity-20 blur-md"></div>

      <div className="max-w-md mx-auto bg-white rounded-3xl shadow-2xl overflow-hidden border border-gray-200 relative z-10">
        {/* Header */}
        <div className="bg-gradient-to-r from-purple-600 to-purple-700 px-6 py-4 text-white">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <NavigationIcon className="w-6 h-6" />
              <div>
                <h1 className="text-lg font-bold">TrafficGuard+</h1>
                <p className="text-purple-100 text-xs">Manila, Philippines</p>
              </div>
            </div>
            <div className="flex gap-2">
              <Bell className="w-5 h-5" />
              <Settings className="w-5 h-5" />
            </div>
          </div>
          <div className="text-center mt-2 text-purple-100 text-sm">
            {currentTime.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
          </div>
        </div>

        {/* Heat Index Alert - Prominent for Hypertension Monitoring */}
        <div className="p-4 border-b bg-gradient-to-r from-red-50 to-orange-50">
          <div className="flex items-center justify-between mb-2">
            <div className="flex items-center gap-2">
              <Thermometer className="w-5 h-5 text-red-500" />
              <span className="font-semibold text-gray-800">Heat Index Monitor</span>
            </div>
            <span className="text-xs bg-orange-100 text-orange-700 px-2 py-1 rounded-full font-medium">
              WARNING
            </span>
          </div>

          {/* Circular Progress Display */}
          <div className="relative w-32 h-32 mx-auto mb-4">
            <div className="w-32 h-32 rounded-full border-8 border-gray-200"></div>
            <div className="absolute top-0 left-0 w-32 h-32 rounded-full border-8 border-purple-500 border-t-transparent transform rotate-45"></div>
            <div className="absolute inset-0 flex items-center justify-center">
              <div className="text-center">
                <div className="text-2xl font-bold text-gray-800">{Math.round(heatIndex)}°F</div>
                <div className="text-xs text-gray-600">TODAY</div>
              </div>
            </div>
          </div>

          <div className="mt-3 p-3 bg-white rounded-lg border-l-4 border-orange-400">
            <p className="text-sm font-medium text-gray-800 mb-1">
              ⚠️ Take frequent breaks
            </p>
            <p className="text-xs text-gray-600">
              High heat can affect blood pressure. Stay hydrated and consider air conditioning.
            </p>
          </div>
        </div>

        {/* Current Route Status */}
        <div className="p-4 bg-green-50 border-b">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-600">Current Route</p>
              <p className="font-semibold text-gray-800">Home → Downtown Office</p>
            </div>
            <div className="text-right">
              <p className="text-lg font-bold text-green-600">24 min</p>
              <p className="text-xs text-gray-500">ETA: 8:45 AM</p>
            </div>
          </div>
        </div>

        {/* Traffic Alerts */}
        <div className="p-4">
          <h3 className="font-semibold text-gray-800 mb-3 flex items-center gap-2">
            <AlertTriangle className="w-4 h-4 text-orange-500" />
            Live Traffic Alerts
          </h3>

          <div className="space-y-3">
            {trafficAlerts.map((alert) => {
              const IconComponent = alert.icon;
              const colorClasses = {
                red: 'border-red-200 bg-red-50 text-red-700',
                orange: 'border-orange-200 bg-orange-50 text-orange-700',
                blue: 'border-blue-200 bg-blue-50 text-blue-700',
                yellow: 'border-yellow-200 bg-yellow-50 text-yellow-700'
              };

              return (
                <div key={alert.id} className={`p-3 border rounded-xl ${colorClasses[alert.color]}`}>
                  <div className="flex items-start gap-3">
                    <IconComponent className="w-5 h-5 mt-0.5 flex-shrink-0" />
                    <div className="flex-1 min-w-0">
                      <p className="font-medium text-sm capitalize">
                        {alert.type === 'police' ? 'Police Checkpoint' : 
                         alert.type === 'construction' ? 'Road Construction' :
                         alert.type === 'accident' ? 'Traffic Accident' : 'Heavy Traffic'}
                      </p>
                      <p className="text-xs opacity-75 truncate">{alert.location}</p>
                      <div className="flex items-center gap-3 mt-1">
                        <span className="text-xs opacity-75">{alert.distance} ahead</span>
                        <span className="text-xs opacity-75">•</span>
                        <span className="text-xs opacity-75">{alert.time}</span>
                      </div>
                    </div>
                    <button className="text-xs px-2 py-1 bg-white bg-opacity-70 rounded-lg font-medium">
                      Avoid
                    </button>
                  </div>
                </div>
              );
            })}
          </div>
        </div>

        {/* Quick Actions */}
        <div className="p-4 bg-gray-50 border-t">
          <div className="grid grid-cols-3 gap-3">
            <button 
              onClick={() => setCurrentPage('navigate')}
              className="p-3 bg-white rounded-xl shadow-sm border hover:bg-gray-50 transition-colors"
            >
              <NavigationIcon className="w-5 h-5 text-blue-600 mx-auto mb-1" />
              <span className="text-xs font-medium text-gray-700">Navigate</span>
            </button>
            <button className="p-3 bg-white rounded-xl shadow-sm border hover:bg-gray-50 transition-colors">
              <Bell className="w-5 h-5 text-green-600 mx-auto mb-1" />
              <span className="text-xs font-medium text-gray-700">Alerts</span>
            </button>
            <button 
              onClick={() => setCurrentPage('health')}
              className="p-3 bg-white rounded-xl shadow-sm border hover:bg-gray-50 transition-colors">
              <Thermometer className="w-5 h-5 text-red-600 mx-auto mb-1" />
              <span className="text-xs font-medium text-gray-700">Health</span>
            </button>
          </div>
        </div>

        {/* Health Reminder Footer */}
        <div className="p-3 bg-purple-600 text-white text-center">
          <p className="text-xs">
            💙 Drive safely. Monitor your health. Stay hydrated.
          </p>
        </div>
      </div>

      {/* Pop-up Card Overlay */}
      {showPopup && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-gradient-to-br from-purple-600 to-purple-700 rounded-3xl p-6 max-w-sm w-full text-white relative shadow-2xl transform animate-pulse">
            {/* Close button */}
            <button 
              onClick={() => setShowPopup(false)}
              className="absolute top-4 right-4 w-8 h-8 bg-white bg-opacity-20 rounded-full flex items-center justify-center hover:bg-opacity-30 transition-all"
            >
              <span className="text-white text-lg leading-none">×</span>
            </button>

            {/* Alert Header */}
            <div className="flex items-center gap-2 mb-4">
              <span className="text-2xl">🚧</span>
              <h3 className="text-lg font-bold">Road Construction Ahead</h3>
              <div className="bg-yellow-400 text-yellow-900 px-2 py-1 rounded-full">
                <span className="text-xs font-bold">⚠</span>
              </div>
            </div>

            {/* Traffic Time */}
            <div className="text-center mb-6">
              <p className="text-purple-100 mb-2">Estimated Time in Traffic</p>
              <p className="text-3xl font-bold">20 mins</p>
            </div>

            {/* Cute Mascot Character */}
            <div className="flex justify-center mb-6">
              <div className="relative">
                {/* Character container - taller to accommodate full body */}
                <div className="w-24 h-40 relative">

                  {/* Head - lifted higher */}
                  <div className="absolute -top-2 left-1/2 transform -translate-x-1/2 w-20 h-20 bg-amber-700 rounded-full relative">

                    {/* Carabao Horns - elongated triangular shapes */}
                    <div className="absolute -top-4 -left-1 w-3 h-8 bg-pink-300 transform -rotate-12 origin-bottom rounded-t-full"></div>
                    <div className="absolute -top-4 -right-1 w-3 h-8 bg-pink-300 transform rotate-12 origin-bottom rounded-t-full"></div>

                    {/* Hair tufts on top - wavy brown sections */}
                    <div className="absolute -top-1 left-4 w-4 h-3 bg-amber-800 rounded-b-full"></div>
                    <div className="absolute -top-1 left-7 w-3 h-3 bg-amber-800 rounded-b-full"></div>
                    <div className="absolute -top-1 right-7 w-3 h-3 bg-amber-800 rounded-b-full"></div>
